    ans := []int{}
    max := 0

    for _, p := range positions {
        left := p[0]
        right := p[0] + p[1]
        height := p[1]
        bottom := 0

        for _, b := range boxes {
            if !(left >= b.Right || right <= b.Left) && bottom < b.Height {
                bottom = b.Height
            }
