func search(nums []int, target int) int {
    left, right := 0, len(nums) -1

    for left < right {
        mid := (left + right) / 2
		// 找到第一个大于等于target的数
        if nums[mid] >= target {
	        right = mid
        }else{
            left = mid + 1
        }
    }

	// 这个数等于target则返回下标
